def callgromacsinrivanna():  from os import system as sys  sys('module load gcc/9.2.0 module load cuda/11.0.228 module load openmpi/3.1.6 module load gcccuda/9.2.0_11.0.228 module load goolfc/9.2.0_3.1.6_11.0.228 module load gromacs/2021.2 cd /scratch/jws6pq/Gromacs/')def OverallRMSD(Protein, Comparison='3merSARS2.pdb'):  from pymol import cmd  proteinpdb = Protein  cmd.load(Comparison, object='CP')  cmd.load(proteinpdb, object='Protein')  cmd.remove('organic')  cmd.load(proteinpdb)  RMSD = cmd.align('CP', 'Protein')[0]  cmd.delete('all')  return RMSDdef TruncateXTCfile(tprfile,xtcfile,newxtcfile,timestepinps):  from os import system as sys  sys('echo 1 | gmx_mpi trjconv -f '+xtcfile+' -s '+tprfile+' -dt '+timestepinps+' -o '+newxtcfile)def CreatingPDBfromtrajectory(tprfile, xtcfile, newpdbfile, timefromsiminps):  from os import system as sys  sys('echo 1 1 | gmx_mpi trjconv -f '+xtcfile+' -center 1 -s '+tprfile+' -dump ' + timefromsiminps + ' -o '+newpdbfile)  sys('echo 1 1 | gmx_mpi trjconv -f '+newpdbfile+' -pbc mol -s '+tprfile+'  -o '+newpdbfile)def CreatingRMSFfile(Timestepinps,tprfile,xtcfile,newxvgfile):  from os import system as sys  sys('echo 1 | gmx_mpi rmsf -dt ' + Timestepinps + ' -res -f '+xtcfile+' -s '+tprfile+' -o '+newxvgfile)