def PieceWise(Protein):    import RBDFinder    cmd.load("SARS2.pdb")    cmd.remove('organic')    proteinpdb = Protein + '.pdb'    cmd.load(proteinpdb)    cmd.select('SARS2NTD', selection='SARS2 and resi 1-233')    cmd.select('SARS2RBD', selection='SARS2 and resi 234-432')    cmd.select('SARS2Stalk', selection='SARS2 and resi 433-973')    SpliceBoundaries=RBDFinder.AlignmentFinder(Protein+'onSARS2.aln', Protein)    SpliceBoundary1=str(SpliceBoundaries[0])    SpliceBoundary2=str(SpliceBoundaries[1])    NTDindex = Protein+' and resi -'+SpliceBoundary1    RBDindex = Protein+' and resi '+ SpliceBoundary1+'-'+SpliceBoundary2    Stalkindex =Protein+ ' and resi '+ SpliceBoundary2+'-'    NTDName = Protein +'NTD'    RBDName = Protein +'RBD'    StalkName = Protein +'Stalk'    cmd.select(NTDName, selection=NTDindex)    NTDRMSD=cmd.align('SARS2NTD', NTDName)[0]    cmd.select(RBDName, selection=RBDindex)    RBDRMSD = cmd.align('SARS2RBD', RBDName)[0]    cmd.select(StalkName, selection=Stalkindex)    StalkRMSD = cmd.align('SARS2Stalk', StalkName)[0]    OverallRMSD = cmd.align('SARS2', Protein)[0]    cmd.delete('all')    return RBDRMSD, OverallRMSDPieceWise('RhinoAlpha')